import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent implements OnInit {

  public eventosFiltrados: any = [];
  public eventos : any = [];
  larguraImagem: number = 200;
  mmargemImagem: number = 2;
  exibirImagem: boolean = true;
  private _filtroLista: string='';

  public get filtroLista():string{
    return this._filtroLista
  }

  public set filtroLista(value:string){

    this._filtroLista = value;

    this.eventosFiltrados = this.filtroLista ? this.filtrarEventos(this.filtroLista) : this.eventos;
  }

  filtrarEventos(filtrarPor: string): any{

       filtrarPor = filtrarPor.toLocaleUpperCase();
       return this.eventos.filter(
        (evento: {tema: string; local:string}) => (evento.tema.toLocaleUpperCase().indexOf(filtrarPor) !== -1) ||
                                                   (evento.local.toLocaleUpperCase().indexOf(filtrarPor) !== -1
        )
  }

  constructor(private http: HttpClient){}

  ngOnInit(): void{
     this.getEventos();

  }

  alterarImagem(){
    this.exibirImagem = !this.exibirImagem;
  }

  public  getEventos(): void{
    this.http.get('https://localhost:5001/api/eventos').subscribe(
      response => {this.eventos = response;
      this.eventosFiltrados = this.eventos
      },
      error => console.log(error) );

  }

}


<h1>Eventos</h1>
<hr>
<div  class="form-inline">
  <div class="form-group mb-2">
      <input type="text" class="form-control mr-2" placeholder="buscar"

      [(ngModel)]="filtroLista"

      >
      <button class="btn btn-outline-success">Buscar</button>
  </div>
</div>
<h3>Filtro: {{filtroLista}} </h3>

<table class="table table-striped">
  <thead class="thead-dark">
    <tr>
      <th>
        <button type="button" class="btn btn-outline-light btn-sm" (click) ="alterarImagem()" >
           {{exibirImagem ? 'mostrar': 'ocultar'}}
        </button>
      </th>
      <th>#</th>
      <th>Tema</th>
      <th>Local</th>
      <th>Data</th>
      <th>Qtd Pessoas</th>
      <th>Lote</th>
      <th>Opções</th>
    </tr>
  </thead>
  <tbody *ngIf="eventos && eventos.length" >
    <tr *ngFor="let item of eventosFiltrados">
      <td>
        <img [collapse]="!exibirImagem"
        [isAnimated]="true"
         src="assets/{{item.imagemUrl}}"
        alt=""
         [style.width.px]="larguraImagem"
         [style.margin.px]="mmargemImagem">
      </td>
      <td>{{item.eventoId}}</td>
      <td>{{item.tema}}</td>
      <td>{{item.local}}</td>
      <td>{{item.dataEvento}}</td>
      <td>{{item.qtdPessoa}}</td>
      <td>{{item.lote}}</td>
    </tr>
  </tbody>
  <tfoot *ngIf="!eventos.length">
    <tr>
      <td colspan="8" class="text-center">
        <h4> Nenhum evento encontrado</h4>
      </td>
    </tr>
  </tfoot>
</table>
